import React, { useEffect, useState } from 'react';
import ClientList from '../components/ClientList';
import ClientForm from '../components/ClientForm';
import { fetchClientsData } from '../services/api';

const ClientsPage = () => {
    const [clients, setClients] = useState([]);
    const [selectedClient, setSelectedClient] = useState(null);

    useEffect(() => {
        const getClients = async () => {
            const data = await fetchClientsData();
            setClients(data);
        };
        getClients();
    }, []);

    const handleEdit = (client) => {
        setSelectedClient(client);
    };

    const handleDelete = async (id) => {
        const updatedClients = clients.filter(client => client.id !== id);
        setClients(updatedClients);
    };

    return (
        <div>
            <h1>Gerenciamento de Clientes</h1>
            <ClientForm selectedClient={selectedClient} setSelectedClient={setSelectedClient} />
            <ClientList clients={clients} onEdit={handleEdit} onDelete={handleDelete} />
        </div>
    );
};

export default ClientsPage;
