import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RentalForm = ({ rentalId, onClose }) => {
    const [vehicles, setVehicles] = useState([]);
    const [clients, setClients] = useState([]);
    const [formData, setFormData] = useState({
        vehicleId: '',
        clientId: '',
        startDate: '',
        endDate: '',
        price: ''
    });

    useEffect(() => {
        const fetchVehicles = async () => {
            const response = await axios.get('/api/vehicles');
            setVehicles(response.data);
        };

        const fetchClients = async () => {
            const response = await axios.get('/api/clients');
            setClients(response.data);
        };

        fetchVehicles();
        fetchClients();

        if (rentalId) {
            const fetchRental = async () => {
                const response = await axios.get(`/api/rentals/${rentalId}`);
                setFormData(response.data);
            };
            fetchRental();
        }
    }, [rentalId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (rentalId) {
            await axios.put(`/api/rentals/${rentalId}`, formData);
        } else {
            await axios.post('/api/rentals', formData);
        }
        onClose();
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>{rentalId ? 'Edit Rental' : 'Create Rental'}</h2>
            <div>
                <label>Vehicle:</label>
                <select name="vehicleId" value={formData.vehicleId} onChange={handleChange} required>
                    <option value="">Select a vehicle</option>
                    {vehicles.map(vehicle => (
                        <option key={vehicle.id} value={vehicle.id}>{vehicle.model}</option>
                    ))}
                </select>
            </div>
            <div>
                <label>Client:</label>
                <select name="clientId" value={formData.clientId} onChange={handleChange} required>
                    <option value="">Select a client</option>
                    {clients.map(client => (
                        <option key={client.id} value={client.id}>{client.name}</option>
                    ))}
                </select>
            </div>
            <div>
                <label>Start Date:</label>
                <input type="datetime-local" name="startDate" value={formData.startDate} onChange={handleChange} required />
            </div>
            <div>
                <label>End Date:</label>
                <input type="datetime-local" name="endDate" value={formData.endDate} onChange={handleChange} required />
            </div>
            <div>
                <label>Price:</label>
                <input type="number" name="price" value={formData.price} onChange={handleChange} required />
            </div>
            <button type="submit">{rentalId ? 'Update Rental' : 'Create Rental'}</button>
            <button type="button" onClick={onClose}>Cancel</button>
        </form>
    );
};

export default RentalForm;
