import React, { useEffect, useState } from 'react';
import RentalList from '../components/RentalList';
import RentalForm from '../components/RentalForm';
import { fetchRentalsData } from '../services/api';

const RentalsPage = () => {
    const [rentals, setRentals] = useState([]);
    const [selectedRental, setSelectedRental] = useState(null);

    useEffect(() => {
        const getRentals = async () => {
            const data = await fetchRentalsData();
            setRentals(data);
        };
        getRentals();
    }, []);

    const handleEdit = (rental) => {
        setSelectedRental(rental);
    };

    const handleDelete = async (id) => {
        // Implement delete functionality here
        const updatedRentals = rentals.filter(rental => rental.id !== id);
        setRentals(updatedRentals);
    };

    return (
        <div>
            <h1>Gerenciamento de Locações</h1>
            <RentalForm selectedRental={selectedRental} setSelectedRental={setSelectedRental} />
            <RentalList rentals={rentals} onEdit={handleEdit} onDelete={handleDelete} />
        </div>
    );
};

export default RentalsPage;
