import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ClientForm = ({ clientId, onClientSaved }) => {
    const [clientData, setClientData] = useState({
        name: '',
        email: '',
        phone: '',
    });

    useEffect(() => {
        if (clientId) {
            // Fetch client data for editing
            axios.get(`/api/clients/${clientId}`)
                .then(response => {
                    setClientData(response.data);
                })
                .catch(error => {
                    console.error('Error fetching client data:', error);
                });
        }
    }, [clientId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setClientData({ ...clientData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (clientId) {
            // Update existing client
            axios.put(`/api/clients/${clientId}`, clientData)
                .then(() => {
                    onClientSaved();
                })
                .catch(error => {
                    console.error('Error updating client:', error);
                });
        } else {
            // Create new client
            axios.post('/api/clients', clientData)
                .then(() => {
                    onClientSaved();
                })
                .catch(error => {
                    console.error('Error creating client:', error);
                });
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Name:</label>
                <input
                    type="text"
                    name="name"
                    value={clientData.name}
                    onChange={handleChange}
                    required
                />
            </div>
            <div>
                <label>Email:</label>
                <input
                    type="email"
                    name="email"
                    value={clientData.email}
                    onChange={handleChange}
                    required
                />
            </div>
            <div>
                <label>Phone:</label>
                <input
                    type="tel"
                    name="phone"
                    value={clientData.phone}
                    onChange={handleChange}
                    required
                />
            </div>
            <button type="submit">{clientId ? 'Update Client' : 'Add Client'}</button>
        </form>
    );
};

export default ClientForm;
