import React, { useEffect, useState } from 'react';
import { getClients } from '../services/api';
import ClientList from '../components/ClientList';
import ClientForm from '../components/ClientForm';

const ClientsPage = () => {
    const [clients, setClients] = useState([]);
    const [selectedClient, setSelectedClient] = useState(null);

    useEffect(() => {
        const fetchClients = async () => {
            const data = await getClients();
            setClients(data);
        };

        fetchClients();
    }, []);

    const handleClientSelect = (client) => {
        setSelectedClient(client);
    };

    const handleClientFormClose = () => {
        setSelectedClient(null);
        // Refresh the client list after adding/editing a client
        const fetchClients = async () => {
            const data = await getClients();
            setClients(data);
        };
        fetchClients();
    };

    return (
        <div>
            <h1>Gerenciamento de Clientes</h1>
            <ClientForm client={selectedClient} onClose={handleClientFormClose} />
            <ClientList clients={clients} onClientSelect={handleClientSelect} />
        </div>
    );
};

export default ClientsPage;
