import React, { useEffect, useState } from 'react';
import { fetchVehiclesData } from '../services/api';
import './VehiclesPage.css';

const VehiclesPage = () => {
    const [vehicles, setVehicles] = useState([]);
    const [filteredVehicles, setFilteredVehicles] = useState([]);
    const [filters, setFilters] = useState({
        brand: '',
        model: '',
        price: '',
        available: '',
    });

    useEffect(() => {
        const loadVehicles = async () => {
            try {
                const data = await fetchVehiclesData();
                setVehicles(data);
                setFilteredVehicles(data);
            } catch (error) {
                console.error('Erro ao carregar veículos:', error);
            }
        };

        loadVehicles();
    }, []);

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilters({ ...filters, [name]: value });

        // Filtrar veículos com base nos critérios
        const filtered = vehicles.filter((vehicle) => {
            return (
                (filters.brand === '' || vehicle.brand.toLowerCase().includes(filters.brand.toLowerCase())) &&
                (filters.model === '' || vehicle.model.toLowerCase().includes(filters.model.toLowerCase())) &&
                (filters.price === '' || vehicle.price <= parseFloat(filters.price)) &&
                (filters.available === '' || vehicle.available.toString() === filters.available)
            );
        });

        setFilteredVehicles(filtered);
    };

    return (
        <div>
            <h1>Gerenciamento de Veículos</h1>
            <div className="filters">
                <input
                    type="text"
                    name="brand"
                    placeholder="Filtrar por marca"
                    value={filters.brand}
                    onChange={handleFilterChange}
                />
                <input
                    type="text"
                    name="model"
                    placeholder="Filtrar por modelo"
                    value={filters.model}
                    onChange={handleFilterChange}
                />
                <input
                    type="number"
                    name="price"
                    placeholder="Filtrar por preço máximo"
                    value={filters.price}
                    onChange={handleFilterChange}
                />
                <select
                    name="available"
                    value={filters.available}
                    onChange={handleFilterChange}
                >
                    <option value="">Disponibilidade</option>
                    <option value="true">Disponível</option>
                    <option value="false">Indisponível</option>
                </select>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>Marca</th>
                        <th>Modelo</th>
                        <th>Ano</th>
                        <th>Placa</th>
                        <th>Disponível</th>
                        <th>Preço</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredVehicles.map((vehicle) => (
                        <tr key={vehicle.id}>
                            <td>{vehicle.brand}</td>
                            <td>{vehicle.model}</td>
                            <td>{vehicle.year}</td>
                            <td>{vehicle.plate}</td>
                            <td>{vehicle.available ? 'Sim' : 'Não'}</td>
                            <td>{vehicle.price}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default VehiclesPage;
