import axios from 'axios';

const api = axios.create({
    baseURL: 'http://servermysqlcn1.mysql.database.azure.com/api', // URL base da API
    headers: {
        'Content-Type': 'application/json',
    },
});

// Funções para gerenciar veículos
export const getVehicles = () => api.get('/vehicles');
export const getVehicleById = (id) => api.get(`/vehicles/${id}`);
export const createVehicle = (vehicle) => api.post('/vehicles', vehicle);
export const updateVehicle = (id, vehicle) => api.put(`/vehicles/${id}`, vehicle);
export const deleteVehicle = (id) => api.delete(`/vehicles/${id}`);

// Funções para gerenciar clientes
export const getClients = () => api.get('/clients');
export const getClientById = (id) => api.get(`/clients/${id}`);
export const createClient = (client) => api.post('/clients', client);
export const updateClient = (id, client) => api.put(`/clients/${id}`, client);
export const deleteClient = (id) => api.delete(`/clients/${id}`);

// Funções para gerenciar locações
export const getRentals = () => api.get('/rentals');
export const getRentalById = (id) => api.get(`/rentals/${id}`);
export const createRental = (rental) => api.post('/rentals', rental);
export const updateRental = (id, rental) => api.put(`/rentals/${id}`, rental);
export const deleteRental = (id) => api.delete(`/rentals/${id}`);

export const fetchRentals = async () => {
    const response = await fetch('http://localhost:5000/api/rentals');
    return response.json();
};
